mapper.update:
update standing_book

    region=#{standBook.region}
    code=#{standBook.code}
    attribute=#{standBook.attribute}
    site_name=#{standBook.siteName}
    contract_name=#{standBook.contractName}
    phone_number=#{standBook.phoneNumber}
    owner_name=#{standBook.ownerName}
    contract_start=#{standBook.contractStart}
    contract_end=#{standBook.contractEnd}
    pay_time=#{standBook.payTime}
    start=#{standBook.start}
    end=#{standBook.end}
    rent=#{standBook.rent}
    tax=#{standBook.tax}

where id=#{standBook.id}

mapper.select:
select * from standing_book


        region = #{param.region}


        and code like '%${param.code}%'


        and site_name like '%${param.siteName}%'


        and UNIX_TIMESTAMP(pay_time)*1000 >= #{param.startTime}


        and UNIX_TIMESTAMP(pay_time)*1000 <= #{param.endTime}
